import React, { Component } from 'react';
import './index.css';

class inputlist extends Component {
	constructor(props) {
		super(props);

		this.state = { a: 'twinkle',
	query:'' };
	}
	handleInputChange = () => {
		this.setState({
			query: this.search.value,
		});
	};

	render() {
		return (
			<form>
				<input ref={input => (this.search = input)} onChange={this.handleInputChange} class="searchinput" />
				<p />
			</form>
		);
	}
}

